import { useState } from "react";

const initialFriends = [
  {
    id: 118836,
    name: "Hamza",
    image: "https://i.pravatar.cc/48?u=118836",
    balance: -45,
  },
  {
    id: 933372,
    name: "Bassma",
    image: "https://i.pravatar.cc/48?u=933372",
    balance: 20,
  },
  {
    id: 499476,
    name: "Zakaria",
    image: "https://i.pravatar.cc/48?u=499476",
    balance: 0,
  },
];

export default function App() {
  const [friendsList, setFriendsList] = useState(initialFriends);
  const [billShow, setBillShow] = useState(false);

  function handleBillshow() {
    setBillShow((s) => !s);
  }
  return (
    <div className="app">
      <div className="sidebar">
        <FriendsList
          friendsList={friendsList}
          handleBillshow={handleBillshow}
        />
        <AddFriend friendsList={friendsList} setFriendsList={setFriendsList} />
      </div>
      <SplitBill billShow={billShow} setBillShow={setBillShow} />
    </div>
  );
}

function FriendsList({ friendsList, handleBillshow }) {
  return (
    <ul>
      {friendsList.map((friend) => (
        <Friend
          friend={friend}
          key={friend.name}
          handleBillshow={handleBillshow}
        />
      ))}
    </ul>
  );
}

function Friend({ friend }) {
  return (
    <li key={friend.name}>
      <h3>{friend.name}</h3>
      <img src={friend.image} alt={friend.name} />
      {friend.balance === 0 ? (
        <p>You and {friend.name} are even</p>
      ) : friend.balance > 0 ? (
        <p className="green">
          {friend.name} owes you {friend.balance}$
        </p>
      ) : friend.balance < 0 ? (
        <p className="red">
          You owe {friend.name} {-friend.balance}$
        </p>
      ) : null}

      <button className="button">Select</button>
    </li>
  );
}

function AddFriend({ friendsList, setFriendsList }) {
  const [isOpen, setIsOpen] = useState(true);
  const [friendName, setFriendName] = useState("");
  const [image, setImage] = useState("https://i.pravatar.cc/48");

  function handleShowAdd() {
    setIsOpen((s) => !s);
    setFriendName("");
    setImage("https://i.pravatar.cc/48");
  }

  function addFriend(e) {
    e.preventDefault();
    const date = Date.now();
    const newFriend = {
      id: date,
      name: friendName,
      image: `${image}?u=${date}`,
      balance: 0,
    };
    setIsOpen(false);
    setFriendsList(() => [...friendsList, newFriend]);
  }

  return isOpen ? (
    <>
      <form className="form-add-friend">
        <label>🧍🏼Friend Name</label>
        <input
          type="text"
          value={friendName}
          onChange={(e) => setFriendName(e.target.value)}
        ></input>

        <label>🌄 Image URL</label>
        <input
          type="text"
          value={image}
          onChange={(e) => setImage(e.target.value)}
        ></input>

        <button className="button" onClick={addFriend}>
          Add
        </button>
      </form>
      <button className="button" onClick={handleShowAdd}>
        Close
      </button>
    </>
  ) : (
    <button className="button" onClick={handleShowAdd}>
      Add Friend
    </button>
  );
}

function SplitBill({ billShow }) {
  return billShow ? (
    <form className="form-split-bill">
      <h2>split the bill with x</h2>

      <label>💰 Bill Value</label>
      <input type="number"></input>

      <label>🙋🏻‍♂️ Your expense</label>
      <input type="number"></input>

      <label>🧍🏼‍♂️ X's expense</label>
      <input type="number" disabled></input>

      <label>🤑 Who is paying the bill </label>
      <select>
        <option value="you">You</option>
        <option value="X">X</option>
      </select>

      <button className="button">Split Bill</button>
    </form>
  ) : null;
}
