import { useState } from "react";

export default function SplitBill({
  selectedFriend,
  friendsList,
  setFriendsList,
}) {
  const friendExpense = bill && bill - yourExpense;
  const [whopaid, setWhopaid] = useState("you");
  const balanceBill = whopaid === "you" ? +friendExpense : -yourExpense;
  function splitBill(e) {
    e.preventDefault();
    setFriendsList(
      friendsList.map((friend) =>
        friend.name === selectedFriend
          ? { ...friend, balance: friend.balance + balanceBill }
          : friend
      )
    );
  }

  return (
    <form className="form-split-bill" onSubmit={splitBill}>
      <h2>split the bill with {selectedFriend}</h2>

      <label htmlFor="bill">üí∞ Bill Value</label>
      <input
        id="bill"
        type="number"
        value={bill}
        onChange={(e) => setBill(e.target.value)}
      ></input>

      <label htmlFor="yourexpense">üôãüèª‚Äç‚ôÇÔ∏è Your expense</label>
      <input
        id="yourexpense"
        type="number"
        value={yourExpense}
        onChange={(e) =>
          setYourExpense(
            Number(e.target.value) > bill ? yourExpense : e.target.value
          )
        }
      ></input>

      <label htmlFor="friendexpense">üßçüèº‚Äç‚ôÇÔ∏è {selectedFriend}'s expense</label>
      <input
        id="friendexpense"
        type="number"
        disabled
        value={friendExpense}
      ></input>

      <label htmlFor="whopaid">ü§ë Who is paying the bill </label>
      <select
        id="whopaid"
        value={whopaid}
        onChange={(e) => setWhopaid(e.target.value)}
      >
        <option value="you">You</option>
        <option value={selectedFriend}>{selectedFriend}</option>
      </select>

      <button className="button">Split Bill</button>
    </form>
  );
}
