import { useState } from "react";
import FriendsList from "./FriendsList";
const initialFriends = [
  {
    id: 118836,
    name: "Hamza",
    image: "https://i.pravatar.cc/48?u=118836",
    balance: -45,
  },
  {
    id: 933372,
    name: "Bassma",
    image: "https://i.pravatar.cc/48?u=933372",
    balance: 20,
  },
  {
    id: 499476,
    name: "Zakaria",
    image: "https://i.pravatar.cc/48?u=499476",
    balance: 0,
  },
];

export default function App() {
  const [friendsList, setFriendsList] = useState(initialFriends);
  const [billShow, setBillShow] = useState(false);
  const [selectedFriend, setSelectedFriend] = useState();
  const [isAddFrOpen, setIsAddFrOpen] = useState();

  function handleBillShow(friend) {
    selectedFriend !== friend.name
      ? setBillShow((s) => true)
      : setBillShow((s) => !s);
    setSelectedFriend(() => friend.name);

    setIsAddFrOpen(false);
  }
  return (
    <div className="app">
      <div className="sidebar">
        <FriendsList
          friendsList={friendsList}
          handleBillShow={handleBillShow}
        />
        <AddFriend
          isOpen={isAddFrOpen}
          setIsOpen={setIsAddFrOpen}
          friendsList={friendsList}
          setFriendsList={setFriendsList}
          billShow={billShow}
          setBillShow={setBillShow}
        />
      </div>
      {billShow && (
        <SplitBill
          setFriendsList={setFriendsList}
          selectedFriend={selectedFriend}
          friendsList={friendsList}
        />
      )}
    </div>
  );
}

function Friend({ friend, handleBillShow }) {
  return (
    <li key={friend.name}>
      <h3>{friend.name}</h3>
      <img src={friend.image} alt={friend.name} />
      {friend.balance === 0 ? (
        <p>You and {friend.name} are even</p>
      ) : friend.balance > 0 ? (
        <p className="green">
          {friend.name} owes you {friend.balance}$
        </p>
      ) : friend.balance < 0 ? (
        <p className="red">
          You owe {friend.name} {-friend.balance}$
        </p>
      ) : null}

      <button className="button" onClick={() => handleBillShow(friend)}>
        Select
      </button>
    </li>
  );
}

function AddFriend({
  friendsList,
  setFriendsList,
  isOpen,
  setIsOpen,
  billShow,
  setBillShow,
}) {
  const [friendName, setFriendName] = useState("");
  const [image, setImage] = useState("https://i.pravatar.cc/48");

  function handleShowAdd() {
    billShow && setBillShow(false);
    setIsOpen((s) => !s);
    setFriendName("");
    setImage("https://i.pravatar.cc/48");
  }

  function addFriend(e) {
    e.preventDefault();
    const date = Date.now();
    const newFriend = {
      id: date,
      name: friendName,
      image: `${image}?u=${date}`,
      balance: 0,
    };
    setIsOpen(false);
    friendName.trim() !== "" &&
      setFriendsList(() => [...friendsList, newFriend]);
  }

  return isOpen ? (
    <>
      <form className="form-add-friend">
        <label htmlFor="friendname">üßçüèºFriend Name</label>
        <input
          id="friendname"
          type="text"
          value={friendName}
          onChange={(e) => setFriendName(e.target.value)}
        ></input>

        <label htmlFor="image">üåÑ Image URL</label>
        <input
          id="image"
          type="text"
          value={image}
          onChange={(e) => setImage(e.target.value)}
        ></input>

        <button className="button" onClick={addFriend}>
          Add
        </button>
      </form>
      <button className="button" onClick={handleShowAdd}>
        Close
      </button>
    </>
  ) : (
    <button className="button" onClick={handleShowAdd}>
      Add Friend
    </button>
  );
}

function SplitBill({ selectedFriend, friendsList, setFriendsList }) {
  const [bill, setBill] = useState("");
  const [yourExpense, setYourExpense] = useState("");
  const friendExpense = bill && bill - yourExpense;
  const [whopaid, setWhopaid] = useState("you");
  const balanceBill = whopaid === "you" ? +friendExpense : -yourExpense;
  console.log(balanceBill);
  function splitBill(e) {
    e.preventDefault();
    console.log(whopaid);
    setFriendsList(
      friendsList.map((friend) =>
        friend.name === selectedFriend
          ? { ...friend, balance: friend.balance + balanceBill }
          : friend
      )
    );
  }

  return (
    <form className="form-split-bill" onSubmit={splitBill}>
      <h2>split the bill with {selectedFriend}</h2>

      <label htmlFor="bill">üí∞ Bill Value</label>
      <input
        id="bill"
        type="number"
        value={bill}
        onChange={(e) => setBill(e.target.value)}
      ></input>

      <label htmlFor="yourexpense">üôãüèª‚Äç‚ôÇÔ∏è Your expense</label>
      <input
        id="yourexpense"
        type="number"
        value={yourExpense}
        onChange={(e) =>
          setYourExpense(
            Number(e.target.value) > bill ? yourExpense : e.target.value
          )
        }
      ></input>

      <label htmlFor="friendexpense">üßçüèº‚Äç‚ôÇÔ∏è {selectedFriend}'s expense</label>
      <input
        id="friendexpense"
        type="number"
        disabled
        value={friendExpense}
      ></input>

      <label htmlFor="whopaid">ü§ë Who is paying the bill </label>
      <select
        id="whopaid"
        value={whopaid}
        onChange={(e) => setWhopaid(e.target.value)}
      >
        <option value="you">You</option>
        <option value={selectedFriend}>{selectedFriend}</option>
      </select>

      <button className="button">Split Bill</button>
    </form>
  );
}
